/* Custom styles for smooth transitions if needed, though Tailwind handles most */
.carousel-item {
  transition: transform 0.5s ease-in-out;
}

/* Basic body styling */
body {
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif; /* A nice readable font */
  @apply text-gray-800 bg-gray-50;
}

/* Custom scrollbar for a nicer look (optional, but can enhance beauty) */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: #f1f1f1;
}

::-webkit-scrollbar-thumb {
  background: #fca5a5; /* Light red/pink */
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: #ef4444; /* Darker red/pink */
}

/* Custom button styles for a consistent look */
.btn-primary {
  @apply bg-pink-500 text-white px-6 py-3 rounded-full hover:bg-pink-600 transition duration-300 ease-in-out shadow-lg;
}

.btn-secondary {
  @apply bg-white text-pink-500 border border-pink-500 px-6 py-3 rounded-full hover:bg-pink-50 transition duration-300 ease-in-out;
}

import React, { useState } from 'react';
