const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className="sticky top-0 z-50 bg-white shadow-md py-4">
      <div className="container mx-auto px-4 flex justify-between items-center">
        {/* Logo */}
        <Link to="/" className="flex items-center space-x-2 text-2xl font-bold text-pink-600">
          <img src="https://img.icons8.com/ios-filled/50/FA5252/birthday-cake.png" alt="Cake Logo" className="h-8 w-8" />
          <span>Sweet Delights</span>
        </Link>

        {/* Nav Links (Desktop) */}
        <div className="hidden md:flex space-x-8">
          <Link to="/" className="text-gray-700 hover:text-pink-600 font-medium transition duration-300">Home</Link>
          <Link to="/products" className="text-gray-700 hover:text-pink-600 font-medium transition duration-300">Products</Link>
          <Link to="/about" className="text-gray-700 hover:text-pink-600 font-medium transition duration-300">About Us</Link>
        </div>

        {/* CTA Button (Desktop) */}
        <div className="hidden md:block">
          <Link to="/products" className="btn-primary">
            Order Now
          </Link>
        </div>

        {/* Mobile Menu Button */}
        <div className="md:hidden">
          <button onClick={() => setIsOpen(!isOpen)} className="text-gray-700 focus:outline-none">
            <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              {isOpen ? (
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
              ) : (
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16"></path>
              )}
            </svg>
          </button>
        </div>
      </div>

      {/* Mobile Menu */}
      {isOpen && (
        <div className="md:hidden bg-white py-4 shadow-inner">
          <div className="flex flex-col items-center space-y-4">
            <Link to="/" onClick={() => setIsOpen(false)} className="text-gray-700 hover:text-pink-600 font-medium text-lg transition duration-300">Home</Link>
            <Link to="/products" onClick={() => setIsOpen(false)} className="text-gray-700 hover:text-pink-600 font-medium text-lg transition duration-300">Products</Link>
            <Link to="/about" onClick={() => setIsOpen(false)} className="text-gray-700 hover:text-pink-600 font-medium text-lg transition duration-300">About Us</Link>
            <Link to="/products" onClick={() => setIsOpen(false)} className="btn-primary w-max">
              Order Now
            </Link>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;

